      const my = this
      this.$nextTick(() => {
        var popoverTriggerList = [].slice.call(document.querySelectorAll('[data-bs-toggle="popover"]'))
        this.popovers = popoverTriggerList.map(function (popoverTriggerEl) {
          return new Popover(popoverTriggerEl, { html: true })
        })

        popoverTriggerList.forEach(popover => {
          popover.addEventListener('shown.bs.popover', function () {
            let closebuttons = document.getElementsByClassName('closebutton')
            for(let closebutton of closebuttons){
              closebutton.addEventListener('click', function () {
                my.popovers.find(p => p["_element"] === popover).hide()
              })
            }
          })
        })
      })
    })
  },
  methods: {
    async fetchStations () {
      const base_url = this.base_data_url ? this.base_data_url + "/sc05_intranet/public/aria/images/meteo" : "/aria"
      const response = await fetch(base_url + '/qa_stations4weboll.json', {
        headers: {
          accept: 'application/json'
        }
      })
      return response.json()
    },
  }
}
</script>

