let aggiornamento: Ref<W37Full> = ref({"id_w37":238,"w37data_set":[{"id_w37_data":198003,"codice_comune":"096043","numeric_value":2,"id_w37":238,"id_time_layouts":49,"id_parametro":"PLUV"},{"id_w37_data":198004,"codice_comune":"096043","numeric_value":3,"id_w37":238,"id_time_layouts":49,"id_parametro":"IDRO"},{"id_w37_data":198005,"codice_comune":"096043","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"STORM"},{"id_w37_data":198006,"codice_comune":"096043","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"DANGER24"},{"id_w37_data":198007,"codice_comune":"001201","numeric_value":2,"id_w37":238,"id_time_layouts":49,"id_parametro":"PLUV"},{"id_w37_data":198008,"codice_comune":"001201","numeric_value":3,"id_w37":238,"id_time_layouts":49,"id_parametro":"IDRO"},{"id_w37_data":198009,"codice_comune":"001201","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"STORM"},{"id_w37_data":198010,"codice_comune":"001201","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"DANGER24"},{"id_w37_data":198011,"codice_comune":"001272","numeric_value":2,"id_w37":238,"id_time_layouts":49,"id_parametro":"PLUV"},{"id_w37_data":198012,"codice_comune":"001272","numeric_value":3,"id_w37":238,"id_time_layouts":49,"id_parametro":"IDRO"},{"id_w37_data":198013,"codice_comune":"001272","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"STORM"},{"id_w37_data":198014,"codice_comune":"001272","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"DANGER24"}],"numero_bollettino":2,"data_emissione":"2024-02-17T01:00:00","ora_emissione":"01:00","situazione_attuale":"situazione 2","previsione_meteo":"meteo 2","previsione_idro":"idro 2","status":"1","last_update":"2023-02-17T08:32:10.579337","username":"weboll","id_w37_parent":null,"mappa_3h":"... (10025 total length)","mappa_24h":"... (10025 total length)","w37_data":{"PLUV":{"49":{"096043":[{"id_w37_data":198003,"codice_comune":"096043","numeric_value":2,"id_w37":238,"id_time_layouts":49,"id_parametro":"PLUV"}],"001201":[{"id_w37_data":198007,"codice_comune":"001201","numeric_value":2,"id_w37":238,"id_time_layouts":49,"id_parametro":"PLUV"}],"001272":[{"id_w37_data":198011,"codice_comune":"001272","numeric_value":2,"id_w37":238,"id_time_layouts":49,"id_parametro":"PLUV"}]}},"IDRO":{"49":{"096043":[{"id_w37_data":198004,"codice_comune":"096043","numeric_value":3,"id_w37":238,"id_time_layouts":49,"id_parametro":"IDRO"}],"001201":[{"id_w37_data":198008,"codice_comune":"001201","numeric_value":3,"id_w37":238,"id_time_layouts":49,"id_parametro":"IDRO"}],"001272":[{"id_w37_data":198012,"codice_comune":"001272","numeric_value":3,"id_w37":238,"id_time_layouts":49,"id_parametro":"IDRO"}]}},"STORM":{"49":{"096043":[{"id_w37_data":198005,"codice_comune":"096043","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"STORM"}],"001201":[{"id_w37_data":198009,"codice_comune":"001201","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"STORM"}],"001272":[{"id_w37_data":198013,"codice_comune":"001272","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"STORM"}]}},"DANGER24":{"49":{"096043":[{"id_w37_data":198006,"codice_comune":"096043","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"DANGER24"}],"001201":[{"id_w37_data":198010,"codice_comune":"001201","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"DANGER24"}],"001272":[{"id_w37_data":198014,"codice_comune":"001272","numeric_value":4,"id_w37":238,"id_time_layouts":49,"id_parametro":"DANGER24"}]}}}})
let state = ref(store.state)
let ready = ref(false)
let saving = ref(false)
let readonly = ref(true)
let today = ref('')

const props = defineProps({
    id: {
        type: String,
        default: () => ''
    },
})


let actions = ref({
  sending:false,
  reopening: false,
})

onMounted(() => {
  aggiornamento_id.value = props.id
  fetchData()
})

async function fetchData() {
  today.value = dateToString(new Date())
  ready.value = false
  // https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Functions/Arrow_functions
  fetchAggiornamento(aggiornamento_id.value).then(response => {
    if (!response.ok) {
      toast.open(
        {
          message: `Errore ${response.status} nel recupero del bollettino`,
          type: 'error',
          position: 'top-left'
        }
      )
    }
    return response.json()
  }).then(async data => {
    
    aggiornamento.value = data
    readonly.value = (aggiornamento.value.status === '1' || aggiornamento.value.status === '2' || !state.value.username)
  }).catch(error => {
    toast.open(
      {
        message: error,
        type: 'error',
        position: 'top-left'
      }
    )
  })
}

async function fetchAggiornamento(id: string | number) {
  const response = await fetch('/api/w37/bulletins/' + id + '/', {
    headers: {
      accept: 'application/json'
    }
  })
  return response
}


function getDateFormatted(rawString: string, time = true) {
  return api.getDateFormatted(rawString, time)
}

function saveW37(newValue: null, id_w37: any, campo: null) {
  saving.value = true
  //console.log(newValue,campo)
  const payload = { }
  payload['id_w37'] = id_w37
  payload['username'] = store.state.username
  if (campo=='ora_aggiornamento')
    payload['ora_aggiornamento'] = newValue
  if (campo=='ora_emissione')
    payload['ora_emissione'] = newValue
  if (campo=='situazione_attuale')
    payload['situazione_attuale'] = newValue
  if (campo=='previsione_meteo')
    payload['previsione_meteo'] = newValue
  if (campo=='previsione_idro')
    payload['previsione_idro'] = newValue

  if (campo==="data_aggiornamento") {
    if(newValue!=null){
      let month = String(newValue.getMonth() + 1);
      let day = String(newValue.getDate());
      const year = String(newValue.getFullYear());

      if (month.length < 2) month = '0' + month;
      if (day.length < 2) day = '0' + day;

      newValue=`${year}-${month}-${day}`;
      payload['data_aggiornamento']=newValue;
    }else{
      payload['data_aggiornamento']=null
    }
      
  }
  bulkUpdateW37(payload).then((response) => {
    if (!response.ok) {
      toast.open(
        {
          message: 'Errore nel salvataggio',
          type: 'error',
          position: 'top-left'
        }
      )
      saving.value = false
    }
    return response.json()
  }).then(data => {
    toast.open(
      {
        message: 'Dato salvato',
        type: 'success',
        position: 'top-left'
      }
    )
    saving.value = false
    aggiornamento.value.last_update = data.last_update
    aggiornamento.value.username = (store.state.username || "")
  }).catch((error) => {
    toast.open(
      {
        message: `Errore di comunicazione: ${error}`,
        type: 'error',
        position: 'top-left'
      }
    )
    saving.value = false
  })
}


function remove() {
  if (
    confirm('Vuoi davvero cancellare questo bollettino?')
  ) {
    api.fetchBulletinDelete(aggiornamento_id.value, 'w37/bulletins', store).then(response => {
      if (response.ok) {
        toast.open(
          {
            message: 'Bollettino cancellato',
            type: 'success',
            position: 'top-left'
          }
        )
        router.back()
      } else {
        toast.open(
          {
            message: `Errore ${response.status} nella cancellazione del bollettino`,
            type: 'error',
            position: 'top-left'
          }
        )
      }
    }).catch(error => {
      toast.open(
        {
          message: error,
          type: 'error',
          position: 'top-left'
        }
      )
    })
  }
}

async function fetchAggiornamentoAction(action: any) {
  const response = await api.fetch_wrapper(
    store.state.access,
    `/api/w37/bulletins/${aggiornamento_id.value}/${action}/`
  )
  return response
}

function execute_timeout(action, reroute, message){
  // console.log("inizio execute_timeout")
  if (saving.value){
    console.log("saving è true faccio partire timeout")
    setTimeout(() => {
      console.log("aspetto 1 secondo finchè non finisce il salvataggio in corso")
      execute_timeout(action, reroute, message)
    }, 1000);
  }else{
    console.log("saving è false lancio execute")
    execute(action, reroute, message)
  }
  // console.log("fine execute_timeout")
}

function execute(action: string, reroute: any, message: any) {
  actions.value[action + 'ing'] = true
  fetchAggiornamentoAction(action).then(response => {
    actions.value[action + 'ing'] = false
    if (response.ok) {
      return response.json()
    } else {
      toast.open(
        {
          message: `Errore ${response.status} nell'esecuzione del comando ${action}`,
          type: 'error',
          position: 'top-left'
        }
      )
    }
  }).then(data => {
    toast.open(
      {
        message: message,
        type: 'success',
        position: 'top-left'
      }
    )
    if (reroute) {
      router.push({ path: `/w37/${data.id_w37}` })
      aggiornamento_id.value = data.id_w37
      fetchData()
    } else {
      fetchData()
    }
  }).catch((error) => {
    this[action + 'ing'] = false
    toast.open(
      {
        message: error,
        type: 'error',
        position: 'top-left'
      }
    )
  })
}

async function bulkUpdateW37(payload: {}) {
  const response = await api.fetch_wrapper(
    store.state.access,
    `/api/w37/bulletins/bulk_update/`,
    {
      method: 'POST',
      body: JSON.stringify(payload)
    }
  )
  return response
}

function dateToString(date){
  const yy = date.getFullYear()
  const mm = date.getMonth() + 1
  const dd = date.getDate()
  return [yy, (mm>9 ? '' : '0') + mm, (dd>9 ? '' : '0') + dd].join('-')
}

